Select tb_level4.level4_id As level4_id, Concat(tb_level1.level1_no, '.', tb_level2.level2_no, '.', tb_level3.level3_no, '.', tb_level4.level4_no, ' - ', tb_level4.level4_nama) As no_nama_akun, tb_level4.jurnal As jurnal, tb_level4.jurnal_kode As jurnal_kode From ((tb_level4 Join tb_level1 On tb_level4.level1_id = tb_level1.level1_id) Join tb_level2 On tb_level4.level2_id = tb_level2.level2_id) Join tb_level3 On tb_level4.level3_id = tb_level3.level3_id
